import React, {useState} from 'react';
import FileBase from 'react-file-base64';
import {useDispatch} from 'react-redux';
import { TextField, Button, Typography, Paper } from '@material-ui/core';

import useStyles from './styles.js';
import {createPost} from '../../actions/posts'
const Form = ()=>{
    const [postData,setPostData] = useState({creator:'',title:'',message:'',tags:'',selectedFile:''});
    const classes = useStyles();
    const dispatch = useDispatch();
    const handleSubmit = (e) => {
        e.preventDefault();

        dispatch(createPost(postData))

    }
    const clear = () => {

    }
    return(
        <Paper className={classes.paper}>
            <form autoComplete='off' noValidate className={`${classes.root} ${classes.form}`} onSubmit={handleSubmit}>
                <Typography variant='h6'>Creeating a Memory</Typography>
                <TextField name='creator' variant='outlined' label='Creator' value={postData.creator} fullWidth onChange={(e)=> setPostData({...postData , creator: e.target.value})} ></TextField>
                <TextField name='title' variant='outlined' label='Title' value={postData.creator} fullWidth onChange={(e)=> setPostData({...postData , creator: e.target.value})} ></TextField>
                <TextField name='message' variant='outlined' label='Message' value={postData.creator} fullWidth onChange={(e)=> setPostData({...postData , creator: e.target.value})}></TextField>
                <TextField name='tags' variant='outlined' label='Tags' value={postData.creator} fullWidth onChange={(e)=> setPostData({...postData , creator: e.target.value})}></TextField>
                <div className={classes.fileInput}><FileBase type='file' multiple={false} onDone = {({base64})=> setPostData({...postData,selectedFile:base64})}></FileBase></div>
                <Button className={classes.buttonSubmit} variant='contained' color='primary' size='large' type='submit' fullWidth>Submit</Button>
                <Button variant='contained' color='secondary' size='small' onClick={clear} fullWidth>Clear</Button>
            </form>

        </Paper>
    )
}

export default Form
