import React, {useState} from 'react';
import { TextField, Button, Typography, Paper } from '@material-ui/core';
import useStyles from './styles.js';
const Form = ()=>{
    const [postData,setPostData] = useState({creator:'',title:'',message:'',tags:'',selectedFile:''});
    const classes = useStyles();
    const handleSubmit = () => {

    }
    return(
        <Paper className={classes.paper}>
            <form autoComplete='off' noValidate className={classes.form} onSubmit={handleSubmit}>
                <Typography variant='h6'>Creeating a Memory</Typography>
                <TextField name='creator' variant='outlined' label='Creator' value={postData.creator} onChange={(e)=> setPostData({...postData , creator: e.target.value})} fullWidth></TextField>
                <TextField name='title' variant='outlined' label='Title' value={postData.creator} onChange={(e)=> setPostData({...postData , creator: e.target.value})} fullWidth></TextField>
                <TextField name='message' variant='outlined' label='Message' value={postData.creator} onChange={(e)=> setPostData({...postData , creator: e.target.value})} fullWidth></TextField>
                <TextField name='tags' variant='outlined' label='Tags' value={postData.creator} onChange={(e)=> setPostData({...postData , creator: e.target.value})} fullWidth></TextField>
                <div className={classes.fileInput}><FileBase type='file' multiple={false} onDone = {({base64})=> setPostData({...postData,selectedFile:base64})}></FileBase></div>
                <Button className={classes.buttonSubmit} variant='container' color='primary' size='large' type='submit' fullWidth>Submit</Button>
            </form>

        </Paper>
    )
}

export default Form
